// import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { useState } from "react";


const LoginScreen = () => {

    let {allFieldsCompleted, setAllFieldsCompleted} = useState(false);

    let { username, setUsername }= useState(""); //use state for these, on change{(e)=> setInput(e.target.value)} or some shit

    let { password, setPassword } = useState("");

    const handleChange = (e) => {
        if(e.target.id === "username") {
            setUsername(e.target.value);
        }
        if (e.target.id === "password") {
            setPassword(e.target.value);
        }

        if (username !== "" && password !== "") {
            setAllFieldsCompleted(true)
        }
    }


    return (
        <>
        <Box component="div" sx={{alignContent: 'center', alignItems: "center",width: '75%', height: '100%', backgroundColor: '#000000'}}>
        <Box
          component="form"
          sx={{
             width: '60%', alignItems: 'center', alignContent: 'center', borderWidth: '5px', borderColor: '#282c34'
          }}
          noValidate
          autoComplete="off"
        >
      <TextField value={username} sx={{width:'40%', display:'block'}} id="username" label="username" variant="outlined" onChange={e => {handleChange(e)}} /><br/>    
      <TextField value={password} sx={{width:'40%', display:'block'}} id="password" label="password" variant="outlined" onChange={e => {handleChange(e)}} /> 
      <Button disabled={allFieldsCompleted? false: true} variant="contained">Log In</Button>
        </Box>
        </Box>
        </>

      );
}

export default LoginScreen;
